import React, { lazy, Suspense, useContext, useEffect } from 'react';
import { Routes, Route, useNavigate, BrowserRouter, Navigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';
import "./App.scss";
import Sidebar from "./Layout/Sidebar/Sidebar";
import MobileHeader from "./Layout/MobileHeader/MobileHeader";
import dotenv from "dotenv";
import ProtectedRoute from './ProtectedRoute'; // Adjust the path based on your file structure

dotenv.config();

function App() {
  const navigate = useNavigate();
  const { user, loading } = useContext(AuthContext); // Fetch user and loading state from context
  const Home = lazy(() => import("./Pages/Home/Home"));
  const CreateTask = lazy(() => import("./Pages/CreateTask/CreateTask"));
  const TaskDetails = lazy(() =>import("./Pages/TaskDetails/TaskDetails"));
  const Login = lazy(() => import('./Pages/Login'));
  const Register = lazy(() => import('./Pages/Register'));

  // Effect for handling redirection logic
  useEffect(() => {
    if (loading) return; // Wait for the loading state to complete
    if (!user && window.location.pathname !== '/login' && window.location.pathname !== '/register') {
      navigate('/login'); // Redirect to login if the user is not authenticated
    } else if (user && window.location.pathname === '/login') {
      navigate('/'); // Redirect to home page if the user is authenticated but on login page
      // Add a slight delay before refreshing the page
      setTimeout(() => {
        window.location.reload(); // Refresh the page after navigation
      }, 300); // You can adjust the delay time if needed
    }
  }, [user, loading, navigate]);

  return (
    // <BrowserRouter>
      <Suspense>
        {/* Sidebar and Mobile Header are rendered only if user is authenticated */}
      {user && <Sidebar />}
      {user && <MobileHeader />}
        {/* <Routes>
          <Route path="/" element={<Home />}  />
          <Route path="*" element={<Home />}  />
          <Route path="/create-task" element={<CreateTask />}  />
          <Route path="/task-details" element={<TaskDetails />}  />
          <Route path="/card-details/:id" element={<TaskDetails  />} />
        </Routes> */}
        <Routes>
  {/* Public Routes */}
  <Route path="/login" element={<Login />} />
  <Route path="/register" element={<Register />} />

  {/* Protected Routes */}
  <Route
    path="/"
    element={
      <ProtectedRoute>
        <Home />
      </ProtectedRoute>
    }
  />
  <Route
    path="/create-task"
    element={
      <ProtectedRoute>
        <CreateTask />
      </ProtectedRoute>
    }
  />
  <Route
    path="/card-details/:id"
    element={
      <ProtectedRoute>
        <TaskDetails />
      </ProtectedRoute>
    }
  />

  {/* Catch-all route */}
  <Route path="*" element={<Navigate to="/" />} />
</Routes>
      </Suspense>
    // </BrowserRouter>
  );
}

export default App;


