import React, { lazy, Suspense, useContext, useEffect } from 'react';
import { Routes, Route, useNavigate, BrowserRouter } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';
import "./App.scss";
import Sidebar from "./Layout/Sidebar/Sidebar";
import MobileHeader from "./Layout/MobileHeader/MobileHeader";
import dotenv from "dotenv";

dotenv.config();

function App() {
  const navigate = useNavigate();
  const { user, loading } = useContext(AuthContext); // Fetch user and loading state from context
  const Home = lazy(() => import("./Pages/Home/Home"));
  const CreateTask = lazy(() => import("./Pages/CreateTask/CreateTask"));
  const TaskDetails = lazy(() =>import("./Pages/TaskDetails/TaskDetails")
  );
  const Login = lazy(() => import('./Pages/Login'));
  const Register = lazy(() => import('./Pages/Register'));

  useEffect(() => {
    if (!user) {
      navigate('/login'); // If not logged in, redirect to login
    } else {
      navigate('/'); // If logged in, navigate to home
    }
  }, [user, navigate]);

  return (
    <BrowserRouter>
      <Suspense>
        <Sidebar />
        <MobileHeader />
        <Routes>
          <Route path="/" element={<Home />} exact />
          <Route path="*" element={<Home />} exact />
          <Route path="/create-task" element={<CreateTask />} exact />
          <Route path="/task-details" element={<TaskDetails />} exact />
          <Route path="/card-details/:id" element={<TaskDetails exact />} />
        </Routes>
      </Suspense>
    </BrowserRouter>
  );
}

export default App;


