import React, { lazy, Suspense, useContext, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import './App.scss';
import Sidebar from './Layout/Sidebar/Sidebar';
import MobileHeader from './Layout/MobileHeader/MobileHeader';
import dotenv from 'dotenv';
import { AuthContext } from './context/AuthContext'; // for files in the src/ folder

dotenv.config();

function App() {
  const navigate = useNavigate();
  const { user, loading } = useContext(AuthContext); // Fetch user and loading state from context

  // Lazy-loaded pages
  const Home = lazy(() => import('./Pages/Home/Home'));
  const CreateTask = lazy(() => import('./Pages/CreateTask/CreateTask'));
  const TaskDetails = lazy(() => import('./Pages/TaskDetails/TaskDetails'));
  const Login = lazy(() => import('./Pages/Login'));
  const Register = lazy(() => import('./Pages/Register'));

  useEffect(() => {
    if (loading) {
      return; // Wait until the loading state is complete
    }

    // If the user is not logged in, redirect to login
    if (!user) {
      navigate('/login');
    } else {
      navigate('/'); // Navigate to home if user is authenticated
    }
  }, [user, loading, navigate]);

  return (
    <Suspense fallback={<div>Loading...</div>}>
      {/* Sidebar and Mobile Header are only shown if user is authenticated */}
      {user && <Sidebar />}
      {user && <MobileHeader />}

      <Routes>
        {/* Public Routes */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Protected Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/create-task" element={<CreateTask />} />
        <Route path="/task-details" element={<TaskDetails />} />
        <Route path="/card-details/:id" element={<TaskDetails />} />

        {/* Catch-all route, redirect to home page */}
        <Route path="*" element={<Home />} />
      </Routes>
    </Suspense>
  );
}

export default App;
